import { useState } from 'react';
import './register.css';
import { useNavigate } from 'react-router-dom';
import Header from './Header';

function Register() {
    const navigate = useNavigate();
    const [activeRole, setActiveRole] = useState('artist'); // default to artist
    const [userInput, setUserInput] = useState({
        firstName: '',
        lastName: '',
        email: '',
        stageName: '', // specific to artists
        companyName: '' // specific to event organizers
    });

    const handleInputChange = (event) => {
        setUserInput({ ...userInput, [event.target.name]: event.target.value });
    };

    const validateAndSubmit = () => {
        // Validation code here
        if (userInput.firstName === '' || userInput.lastName === '' || userInput.email === '') {
            alert('Please fill in all required fields');
            return;
        }
        var emailValidation = /\S+@\S+\.\S+/;
        if (!userInput.email.match(emailValidation)) {
            alert('Enter a valid email address');
            return;
        }

        console.log(userInput);
        navigate('/login'); // navigate to login page after registration
    };

    return (
        <div>
            <Header />
            <h2>Register</h2>
            <div className="role-tabs">
                <button onClick={() => setActiveRole('artist')}>Artist</button>
                <button onClick={() => setActiveRole('eventOrganizer')}>Event Organizer</button>
                <button onClick={() => setActiveRole('attendee')}>Attendee</button>
            </div>
            <form onSubmit={(e) => { e.preventDefault(); validateAndSubmit(); }}>
                <div className="form-group">
                    <label>First Name</label>
                    <input type="text" name="firstName" value={userInput.firstName} onChange={handleInputChange} className="control-form" />
                </div>
                <div className="form-group">
                    <label>Last Name</label>
                    <input type="text" name="lastName" value={userInput.lastName} onChange={handleInputChange} className="control-form" />
                </div>
                <div className="form-group">
                    <label>Email</label>
                    <input type="text" name="email" value={userInput.email} onChange={handleInputChange} className="control-form" />
                </div>

                {activeRole === 'artist' && (
                    <div className="form-group">
                        <label>Stage Name</label>
                        <input type="text" name="stageName" value={userInput.stageName} onChange={handleInputChange} className="control-form" />
                    </div>
                )}

                {activeRole === 'eventOrganizer' && (
                    <div className="form-group">
                        <label>Company Name</label>
                        <input type="text" name="companyName" value={userInput.companyName} onChange={handleInputChange} className="control-form" />
                    </div>
                )}

                {activeRole === 'attendee' && (
                    <div className="form-group">
                        {/* Include any specific fields for attendees if necessary */}
                    </div>
                )}

                <button type="submit">Submit</button>
            </form>
        </div>
    );
}

export default Register;
